describe("RequestCache", () => {
  it("deduplicates concurrent requests", async () => {
    let callCount = 0;
    const fetcher = async () => {
      callCount++;
      await delay(100);
      return "result";
    };

    // Make 5 concurrent requests
    const results = await Promise.all([
      requestCache.dedupe("key", fetcher),
      requestCache.dedupe("key", fetcher),
      requestCache.dedupe("key", fetcher),
      requestCache.dedupe("key", fetcher),
